mapper.select:
SELECT * FROM post WHERE member_Id = #{member_Id}
AND post_public = 'y'
ORDER BY post_Seq DESC

mapper.update:
update post
set post_content     = #{post_Content},
    post_public  	= #{post_Public},
    post_image_count = #{post_Image_Count}
where post_seq = #{post_Seq}

mapper.select:
SELECT post_Seq
FROM post
WHERE member_Id = #{member_Id}
